mapper.update:
UPDATE Post
SET
      post_Title = #{postTitle}
    , post_Contents = #{postContents}
    , post_Category = #{postCategory}
    , post_Score = #{postScore}
    , post_Correct = NOW()

WHERE
    post_Number = #{postNumber}
